import { useState } from "react";
import { useHistory, withRouter } from "react-router-dom";
import { auth } from "../utils/auth";
import InfoTooltip from "./InfoTooltip";

function Register(props) {
  const [userData, setUserData] = useState({
    email: "",
    password: "",
  });

  const [isTooltip, setIsTooltip] = useState(false);
  const history = useHistory();

  function handleChange(e) {
    const { name, value } = e.target;
    setUserData({
      ...userData,
      [name]: value,
    });
  }

  function handleSubmit(e) {
    e.preventDefault();
    auth.signUp(userData)
    .then(() => {
      setIsTooltip(true);
      history.push("/sign-in");
    })
    .catch((err) => {
      console.log(err);
      setIsTooltip(false);
    })
  }

  // function handleClose() {
  //   setIsTooltip({
  //     ...props,
  //     isOpen: false
  //   });
  //   if ()
  // }

  return (
    <main className="auth">
      <h2 className="form__title-auth">Регистрация</h2>
      <form className="form" name="login" onSubmit={handleSubmit}>
        <fieldset className="form__container">
          <input
            value={userData.email}
            onChange={handleChange}
            id="email-input"
            name="email"
            type="email"
            className="form__item form__item_type_auth"
            placeholder="Email"
            required
          />
          <span className="email-input-error form__item-error"></span>
          <input
            value={userData.password}
            onChange={handleChange}
            id="metier-input"
            name="description"
            type="text"
            className="form__item form__item_type_auth"
            placeholder="Пароль"
            required
          />
          <span className="password-input-error form__item-error"></span>
        </fieldset>
        <button type="submit" className="form__submit_type_auth">
          Зарегистрироваться
        </button>
        <div className="form__link-container">
          <a className="form__link" href="/sing-in">
            Уже зарегистрированы? Войти
          </a>
        </div>
      </form>
    </main>
  );
}

export default withRouter(Register);
